jQuery(document).ready(function() {
	/*---[ Widget Slider ]---*/
  jQuery('.widgetslider').imagesLoaded(function () {
    jQuery('.widgetslider').flexslider({
  		animation: "fade",
  		pauseOnHover: true,
  		smoothHeight: true,
  		controlNav: false,
  		prevText: "<i class='fa fa-angle-left'></i>",
  		nextText: "<i class='fa fa-angle-right'></i>",
  		touch: true,
  		start: function(slider) {
              jQuery('.widgetslider').removeClass('loading');
  		}
    });
  });

  /*---[ Featured Slider ]---*/
  jQuery('.featuredslider').imagesLoaded(function () {
    jQuery('.featuredslider').flexslider({
		animation: "fade",
		pauseOnHover: true,
		smoothHeight: true,
		controlNav: false,
		prevText: "<i class='fa fa-angle-left'></i>",
		nextText: "<i class='fa fa-angle-right'></i>",
		touch: true,
        initDelay: 5000,
		start: function(slider) {
            jQuery('.featuredslider').removeClass('loading');
		}
    });
  });

	/*---[ Gallery Post Format Slider ]---*/
  jQuery('.galleryslider').imagesLoaded(function () {
  	jQuery('.galleryslider').flexslider({
  		animation: "fade",
  		pauseOnHover: true,
  		smoothHeight: true,
  		directionNav: true,
  		controlNav: true,
  		prevText: "<i class='fa fa-angle-left'></i>",
  		nextText: "<i class='fa fa-angle-right'></i>",
  		touch: true,
  		start: function(slider) {
              jQuery('.galleryslider').removeClass('loading');
  		}
  	});
  });
});

/*---[ Cover Image ]---*/
jQuery(document).ready(function(){
    jQuery('.cover-image').each(function(){
        var $bgobj = jQuery(this); // assigning the object
     
        jQuery(window).scroll(function() {
            var yPos = -(jQuery(window).scrollTop() / $bgobj.data('speed')); 
             
            // Put together our final background position
            var coords = '50% '+ yPos + 'px';
 
            // Move the background
            $bgobj.css({ backgroundPosition: coords });
        }); 
    });    
});

/*---[ Fitvids ]---*/
jQuery(document).ready(function( $ ){
	$("body").fitVids( {
		customSelector: [
			"iframe[src*='youtu.be']",
			"iframe[src*='soundcloud.com']",
			"iframe[src*='mixcloud.com']",
			"iframe[src*='blip.tv']",
			"iframe[src*='dailymotion.com']",
			"iframe[src*='spotify.com']",
			"iframe[src*='slideshare.net']",
		]
	} );
	$( ".wp-video-shortcode, .wp-audio-shortcode" ).css( 'max-width', '100%' );
});

/*---[ Header Search ]---*/
jQuery(document).ready(function(){
  jQuery(".header-search").hover(
    function(){
      jQuery("header .s").show();
    }, 
     function () {
       jQuery('header .s').hide();
     }
  );
});


